        copymap(acopyaction, b2g(parentstructrowid)),
        name,
        targettype,
        queuename,
        generatorprogid,
        generatorstyle,
        thedevelopmentenv

      FROM generator_target
      WHERE parentstructrowid = g2b(aparentstructrowid);
