body:
  BEGIN
    DECLARE ausersid binary(16);
    DECLARE apass varchar(80);


    DECLARE IsOK int;
    SELECT
      0 INTO IsOK;

    SELECT
      1 INTO IsOK
    FROM the_Session
    WHERE (closed = 1)
    AND the_Session.the_Sessionid = g2b(acursessionid);
    IF IsOK <> 0 THEN
      SELECT
        'Session closed' result;
      LEAVE body;
    END IF;

    IF anewpassword <> acomppassword THEN
      SELECT
        'Новый пароль не совпадает с проверочным значением' result;
      LEAVE body;
    END IF;

    SELECT
      users.usersid,
      users.password INTO ausersid, apass
    FROM the_Session
      JOIN users
        ON the_session.usersid = users.usersid
    WHERE the_Session.the_Sessionid = g2b(acursessionid);
    IF apass <> md5(aoldpassword) THEN
      SELECT
        'Неверный старый пароль' result;
    ELSE
      UPDATE users
      SET password = md5(anewpassword)
      WHERE usersid = ausersid;
      SELECT
        'OK' result;
    END IF;


  END
  $$

