      ELSE
        --  insert  --
        --  verify access  --
        SELECT
          checkoperation(acursession, 'users.add') INTO aaccess;
        IF aaccess = 0 THEN
          SELECT
            'добавление строк не разрешено. раздел=users' result;
          LEAVE body;
        END IF;




        START TRANSACTION;
          INSERT INTO users (usersid
          , instanceid
