--
CREATE PROCEDURE user_register (alogin varchar(64), apass varchar(80), afirst varchar(80), alast varchar(80), aphone varchar(20), aemail varchar(128))
body:
  BEGIN
    DECLARE ausersid binary(16);
    DECLARE ID binary(16);


    DECLARE IsOK int;
    SELECT
      0 INTO IsOK;

    SELECT
      1 INTO IsOK
    FROM users
    WHERE login = alogin;
    IF IsOK <> 0 THEN
      SELECT
        concat('Извините,логин:', alogin, ' уже занят') result;
      LEAVE body;
    END IF;


    SELECT
      instanceid INTO id
    FROM instance
    WHERE objtype = 'MTZUsers';

    SELECT
      g2b(uuid()) INTO ausersid;
    INSERT INTO users (changestamp, instanceid, usersid, family, name, surname, login, password)
      VALUES (sysdate(), id, ausersid, alast, afirst, ' ', alogin, md5(apass));

    SELECT
      g2b(uuid()) INTO id;
    INSERT INTO instance (instanceid, objtype, name, changestamp)
      VALUES (id, 'arc', concat(afirst, ' ', alast), sysdate());


    INSERT INTO arc_info (instanceid, arc_infoid, changestamp, timestamp, family, name, phone, email, login, isadmin)
      VALUES (id, ausersid, NOW(), NOW(), alast, afirst, aphone, aemail, alogin, 0);

    SELECT
      'OK' result;


  END
  $$

