      WHEN 2 THEN SELECT
            concat(abrief,
            'приложение; ') INTO abrief;
      WHEN 4 THEN SELECT
            concat(abrief,
            'арм; ') INTO abrief;
      ELSE SELECT
          concat(abrief,
          '; ') INTO abrief;
    END CASE;
  ELSE
    SET abrief = '';
  END IF;
  RETURN abrief;
END
$$

