--
-- Описание для функции getclientsms
--
CREATE FUNCTION getclientsms (aclientid varchar(38)
, arowid varchar(38))
RETURNS tinyint(1)
READS SQL DATA
BEGIN
  DECLARE existscnt int;

  SELECT
    COUNT(*) INTO existscnt
  FROM arsms_data
    JOIN arc_md
      ON arc_md.moduleserial = arsms_data.serialno
  WHERE arc_md.instanceid = g2b(aclientid)
  AND arsms_dataid = g2b(arowid);
  IF existscnt = 0 THEN
    RETURN 0;
  END IF;

  RETURN 1;
END
$$

