(x'25AD0CC1ED9518459177E29824F01E47', x'0C660F7FF6D5FE44AFF366FA89034FF5', '2012-01-20 14:40:23', '2017-07-20 19:30:20', NULL, NULL, NULL, 'Login', 'Login', 'create or replace procedure Login\r\n  \r\n(aTHE_SESSION out  CHAR/* Идентификатор новой сессии */  \r\n  \r\n,aPWD VARCHAR2/* Пароль */  \r\n  \r\n,aUSR VARCHAR2/* Имя пользователя */  \r\n  \r\n)  \r\n as   \r\n  \r\naID char(38);   \r\naUSERSID char(38);   \r\nasysid char(38);   \r\nSysLogid char(38); \r\nexistsCnt integer;   \r\nbegin   \r\nasysid := null;   \r\nselect instanceid into asysid from instance where objtype = ''MTZSYSTEM'';   \r\nathe_session:=null  ;   \r\nif aPWD is null   \r\nthen   \r\n select count(*) into existsCnt from users where Login=ausr and Password is null;   \r\n  if existsCNt =1   \r\n  then   \r\n    select USERSID into ausersid from users where Login=ausr and Password is null;   \r\n    select  mtz.newid() into aID from sys.dual;   \r\n       if asysid is null then   \r\n        insert into the_session(the_sessionid,lastaccess,usersid,closed,startAt)   \r\n        values(aid,sysdate,ausersid,0,sysdate);   \r\n       else   \r\n        insert into the_session(instanceid,the_sessionid,lastaccess,usersid,closed,startAt)   \r\n        values(asysid,aid,sysdate,ausersid,0,sysdate);   \r\n       end if;   \r\n     athe_session:=aid;   \r\n      \r\n     SysLogid:=MTZ.NEWID(); \r\n     MTZSystem.SysLog_SAVE( aTheSession=>aid,aCURSESSION=>aid, aInstanceID=>asysid, aSysLogid=>SysLogid, aLogStructID =>ausr, \r\n     aVERB=>''LOGIN_SUCCESS'', aThe_Resource=>ausr,aLogInstanceID=>null); \r\n  else \r\n     SysLogid:=MTZ.NEWID(); \r\n     MTZSystem.SysLog_SAVE( aTheSession=>aid,aCURSESSION=>aid, aInstanceID=>asysid, aSysLogid=>SysLogid, aLogStructID =>ausr, \r\n     aVERB=>''LOGIN_FAILED'', aThe_Resource=>ausr,aLogInstanceID=>null); \r\n  End if;   \r\nelse   \r\n   \r\n  select count(*) into existsCnt from users where Login=ausr and Password =aPWD;   \r\n  if existsCNt =1   \r\n  then   \r\n   select USERSID into ausersid from users where Login=ausr and Password =aPWD;   \r\n   select  mtz.newid() into aID from sys.dual;   \r\n       if asysid is null then   \r\n        insert into the_session(the_sessionid,lastaccess,usersid,closed,startAt)   \r\n        values(aid,sysdate,ausersid,0,sysdate);   \r\n       else   \r\n        insert into the_session(instanceid,the_sessionid,lastaccess,usersid,closed,startAt)   \r\n        values(asysid,aid,sysdate,ausersid,0,sysdate);   \r\n       end if;   \r\n     athe_session:=aid;   \r\n      \r\n     SysLogid:=MTZ.NEWID(); \r\n     MTZSystem.SysLog_SAVE( aTheSession=>aid,aCURSESSION=>aid, aInstanceID=>asysid, aSysLogid=>SysLogid, aLogStructID =>ausr, \r\n     aVERB=>''LOGIN_SUCCESS'', aThe_Resource=>ausr,aLogInstanceID=>null);  \r\n      \r\n  else   \r\n    select count(*) into existsCnt from users where DomaiName=ausr and aPWD=''INTEGRATED'';   \r\n    if existsCNt =1   \r\n    then   \r\n     select USERSID into ausersid from users where DomaiName =ausr ;   \r\n     select  mtz.newid() into aID from sys.dual;   \r\n       if asysid is null then   \r\n        insert into the_session(the_sessionid,lastaccess,usersid,closed,startAt)   \r\n        values(aid,sysdate,ausersid,0,sysdate);   \r\n   \r\n       else   \r\n        insert into the_session(instanceid,the_sessionid,lastaccess,usersid,closed,startAt)   \r\n        values(asysid,aid,sysdate,ausersid,0,sysdate);   \r\n       end if;   \r\n     athe_session:=aid;   \r\n      \r\n     SysLogid:=MTZ.NEWID(); \r\n     MTZSystem.SysLog_SAVE( aTheSession=>aid,aCURSESSION=>aid, aInstanceID=>asysid, aSysLogid=>SysLogid, aLogStructID =>ausr, \r\n     aVERB=>''LOGIN_SUCCESS'', aThe_Resource=>ausr,aLogInstanceID=>null); \r\n    else \r\n     SysLogid:=MTZ.NEWID(); \r\n     MTZSystem.SysLog_SAVE( aTheSession=>aid,aCURSESSION=>aid, aInstanceID=>asysid, aSysLogid=>SysLogid, aLogStructID =>ausr, \r\n     aVERB=>''LOGIN_FAILED'', aThe_Resource=>ausr,aLogInstanceID=>null); \r\n    End if;   \r\n  end if;   \r\nend if; -- password is null   \r\nend;   \r\n  '),
(x'25AD0CC1ED9518459177E29824F01E47', x'2248998532DBB944B94F8C6666DFFAB7', '2012-01-20 14:40:23', '2017-07-20 19:30:20', NULL, NULL, NULL, 'NEWID', 'NEWID', 'create or replace function NEWID return varchar2 as\r\n  Result varchar2(40);\r\n  lguid raw(128);\r\n  sTempString varchar2(40);\r\nbegin\r\n  \r\n  \r\n  \r\n  select sys_guid() into lguid from dual;\r\n  \r\n  sTempString:=to_char(lguid);\r\n \r\n  Result:=''{'' || \r\n              Substr(sTempString, 1, 8) \r\n              || ''-'' ||\r\n              Substr(sTempString, 9, 4) \r\n              || ''-'' ||\r\n              Substr(sTempString, 13, 4) \r\n              || ''-'' ||\r\n              Substr(sTempString, 17, 4) \r\n              || ''-'' ||\r\n              Substr(sTempString, 21, 12) \r\n              || ''}'';\r\n  return(Result);\r\nend NEWID;'),
(x'C4CF9CB244736A448F14824C92DC2D30', x'47B4A592397A5947AA047B297879E4E9', '2012-01-20 14:40:23', '2017-07-20 19:30:21', NULL, NULL, NULL, 'индексы', NULL, 'CREATE  INDEX Syslog_times ON SysLog(ChangeStamp) \r\n$$\r\n\r\ncreate index instance_lockuser on instance(lockuserid)\r\n\r\n$$\r\n\r\ncreate index instance_locksession on instance(locksessionid)\r\n\r\n$$\r\n\r\n \r\n'),
(x'C4CF9CB244736A448F14824C92DC2D30', x'494A11821D40DB449D4CCBB1F8A2EA97', '2012-01-20 14:40:23', '2017-07-20 19:30:21', NULL, NULL, NULL, 'V_ROLEENTRY', 'V_ROLEENTRY', 'drop view if exists V_RoleEntryPoints \r\n$$\r\ncreate view V_RoleEntryPoints as\r\nselect \r\nRoles_WP.instanceid ROLEID ,\r\nRoles_WP.WP ARMID,\r\nRoles_ACT.Accesible,\r\nEntrypoints.Name,\r\nEntrypoints.Caption\r\nfrom ROLES_WP \r\njoin ROLES_ACT on ROLES_WP.ROLES_WPID=ROLES_ACT.parentstructrowid\r\njoin entrypoints on \r\nROLES_ACT.entrypoints=entrypoints.entrypointsid\r\n$$'),
