--
-- Описание для триггера arc_info_inserted
--
CREATE TRIGGER arc_info_inserted
AFTER INSERT
ON arc_info
FOR EACH ROW
BEGIN
  DECLARE existsCnt integer;
  DECLARE ID binary(16);
  IF new.login <> '' THEN
    SELECT
      COUNT(*) INTO existsCnt
    FROM users
    WHERE login = new.login
    OR usersid = new.arc_infoid;
    IF existsCnt = 0 THEN
      SELECT
        instanceid INTO id
      FROM instance
      WHERE objtype = 'MTZUsers';
      INSERT INTO users (changestamp, instanceid, usersid, family, name, surname, login, password)
        VALUES (sysdate(), id, new.arc_infoid, new.family, new.name, '', new.login, md5(new.login));
    ELSE
      UPDATE users
      SET family = new.family,
          name = new.name,
          login = new.login
      WHERE usersid = new.arc_infoid;
      UPDATE users
      SET family = new.family,
          name = new.name
      WHERE login = new.login;
    END IF;
  END IF;

END
$$

--
-- Описание для триггера arc_info_updated
--
CREATE TRIGGER arc_info_updated
AFTER UPDATE
ON arc_info
FOR EACH ROW
BEGIN
  DECLARE existsCnt integer;
  DECLARE ID binary(16);
  SELECT
    instanceid INTO id
  FROM instance
  WHERE objtype = 'MTZUsers';

  IF new.login <> old.login THEN

    SELECT
      COUNT(*) INTO existsCnt
    FROM users
    WHERE usersid = new.arc_infoid;
    IF existsCnt = 0 THEN

      SELECT
        COUNT(*) INTO existsCnt
      FROM users
      WHERE login = old.login;
      IF existsCnt = 0 THEN
        INSERT INTO users (changestamp, instanceid, usersid, family, name, surname, login, password)
          VALUES (sysdate(), id, new.arc_infoid, new.family, new.name, ' ', new.login, md5(new.login));
      ELSE
        UPDATE users
        SET login = new.login,
            password = md5(new.login)
        WHERE login = old.login;
        UPDATE users
        SET family = new.family,
            name = new.name
        WHERE login = new.login;
      END IF;
    ELSE
      UPDATE users
      SET login = new.login,
          password = md5(new.login)
      WHERE login = old.login;
      UPDATE users
      SET login = new.login,
          password = md5(new.login)
      WHERE usersid = new.arc_infoid;
      UPDATE users
      SET family = new.family,
          name = new.name
      WHERE usersid = new.arc_infoid;
    END IF;

  ELSE
    SELECT
      COUNT(*) INTO existsCnt
    FROM users
    WHERE login = old.login
    OR usersid = new.arc_infoid;
    IF existsCnt = 0 THEN
      INSERT INTO users (changestamp, instanceid, usersid, family, name, surname, login, password)
        VALUES (sysdate(), id, new.arc_infoid, new.family, new.name, ' ', new.login, md5(new.login));
    END IF;

    UPDATE users
    SET family = new.family,
        name = new.name
    WHERE login = new.login
    OR usersid = new.arc_infoid;
  END IF;

END
$$

